mapper.select:
SELECT userId, passWord AS password, nickName, createTime, isWithDraw, status
FROM user
WHERE userId = #{id}

mapper.update:
UPDATE user
SET passWord=#{password},
    nickName=#{nickName},
    isWithDraw=#{isWithDraw},
    status=#{status}
WHERE userId = #{userId}

mapper.select:
SELECT userId, passWord AS password, nickName, createTime, isWithDraw, status
FROM user
WHERE userId = #{id}
  AND passWord = #{password}
  AND status != 'DELETE'

SELECT COUNT(*)
FROM user
WHERE userId = #{id}

mapper.update:
UPDATE user
SET passWord = #{password}
WHERE userId = #{userId}
